operation.ignoreif:
	$('#button-cart').after('<div id="quantity-alert" class="alert-danger text-center">' + json['error'] + '</div>'); 
  $('#button-cart').removeClass('btn-primary');
  $('#button-cart').addClass('btn-danger');
  setTimeout(
  function() 
  {
    $('#button-cart').removeClass('btn-danger');
    $('#button-cart').addClass('btn-primary');
    $('#quantity-alert').remove();
  }, 3000);

operation.add:
	$('#button-cart').after('<div id="quantity-alert" class="alert-danger text-center">' + json['error'] + '</div>'); 
  $('#button-cart').removeClass('btn-primary');
  $('#button-cart').addClass('btn-danger');
  setTimeout(
  function() 
  {
    $('#button-cart').removeClass('btn-danger');
    $('#button-cart').addClass('btn-primary');
    $('#quantity-alert').remove();
  }, 3000);
